package com.test.webflux.util;import com.power.common.model.CommonResult;import com.power.common.util.DateTimeUtil;import com.test.webflux.enums.ErrorCodeEnum;/** * * * @author yu on 2018/09/29. */public class ResultUtil {    /**     * return success     *     * @param data     * @return     */    public static <T> CommonResult<T> success(T data) {        CommonResult<T> result = new CommonResult();        result.setCode(ErrorCodeEnum.SUCCESS.getCode());        result.setSuccess(true);        result.setData(data);        result.setMessage(ErrorCodeEnum.SUCCESS.getDesc());        result.setTimestamp(DateTimeUtil.long2Str(System.currentTimeMillis(),"yyyy-MM-dd HH:mm:ss"));        return result;    }    /**     * return error     *     * @param code error code     * @param msg  error message     * @return     */    public static CommonResult error(String code, String msg) {        CommonResult result = new CommonResult();        result.setCode(code);        result.setData("");        result.setMessage(msg);        result.setTimestamp(DateTimeUtil.long2Str(System.currentTimeMillis(),"yyyy-MM-dd HH:mm:ss"));        return result;    }    /**     * use enum     *     * @param status     * @return     */    public static CommonResult error(ErrorCodeEnum status) {        return error(status.getCode(), status.getDesc());    }}